import React from 'react'
import { Modal, ModalBody, ModalFooter, ModalHeader, Button } from 'reactstrap'
import axios from 'axios'
import apiconfig from '../../../configs/api.config.json'
import API from '../../../helpers/API';

class CreateMenu extends React.Component{
    constructor (props){
        super(props)
        let userdata = JSON.parse(localStorage.getItem(apiconfig.LS.USERDATA))
        this.state = {
            formData:{
                kode_agama :'',
                deskripsi :'',
                created_by :userdata.username
            }
        }
        this.submitHandler=this.submitHandler.bind(this)
        this.changeHandler=this.changeHandler.bind(this)
    }
    changeHandler(e){
        let tmp=this.state.formData
        tmp[e.target.name]=e.target.value
        this.setState({
            formData : tmp
        })
    }
    submitHandler(){
        let token=localStorage.getItem(apiconfig.LS.TOKEN)
        let option = {
            url: apiconfig.BASE_URL+apiconfig.ENDPOINTS.AGAMA,
            method: "post",
            headers:{
                "authorization": token,
                "Content-Type" : "application/json"
            },
            data: this.state.formData
        }
        axios(option)
        .then((response)=>{
            if(response.data.code === 200){
                this.props.closeHandler()
                alert('Success')
            } else {
                alert(response.data.message)
            }
        })
        .catch((error)=>{
            console.log(error);            
        })
    }

    render(){
        return(

            <Modal isOpen={this.props.show} className={this.props.className}>
                <ModalHeader> Tambah Agama</ModalHeader>
                <ModalBody >
                <table>
                    <tr>
                        <td style={{width: '40%'}}>Kode Agama</td>
                        <td style={{width: '10%'}}>:</td>
                        <td style={{width: '50%'}}>
                            <div class ="input-group mb-3 input-group-sm">
                                <input type="text" class="form-control" placeholder="Kode agama"
                                name="kode_agama" value={this.state.formData.kode_agama} onChange={this.changeHandler} required />
                            </div>
                        </td>
                    </tr>
                    <tr>
                        <td>Deskripsi</td>
                        <td>:</td>
                        <td>
                            <div class ="input-group mb-3 input-group-sm">
                                <input type="text" class="form-control" placeholder="Deskripsi" 
                                name="deskripsi" value={this.state.formData.deskripsi} onChange={this.changeHandler} required/>
                            </div>
                        </td>
                    </tr>
                </table>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick = {this.submitHandler}>Save</Button>
                    <Button color="warning" onClick = {this.props.closeHandler}>Cancel</Button>
                </ModalFooter>
        </Modal>
        )
    }
}
export default CreateMenu
