// import apiconfig from '../configs/api.config.json'

const trimString = str => {
    return str.replace(/^\s+|\s+$/gm,'')
}

const FormValidation = {
    createReqDesign: (design, design_item, callback) => {
        let message = "success"
        design.note = trimString(design.note)
        design.title_header = trimString(design.title_header)

        if (design.t_event_id === "") {
            message = "Pilih event terlebih dahulu"
            return callback(0, message)
        }
        if (design.title_header === "") {
            message = "Isi data Design Title terlebih dahulu" 
            return callback(0, message)
        }
        design_item.map((el, keys) => {
            el.title_item = trimString(el.title_item)
            el.request_pic = trimString(el.request_pic)
            el.note = trimString(el.note)
            if (el.m_product_id === "") {
                message = "Pilih jenis product terlebih dahulu (baris "+Number(keys+1)+")"
                return callback(0, message)
            }
            if (el.title_item === "") {
                message = "Isi kolom Title terlebih dahulu (baris "+Number(keys+1)+")"
                return callback(0, message)
            }
            if (el.request_pic === "") {
                message = "Isi kolom Request PIC terlebih dahulu (baris "+Number(keys+1)+")"
                return callback(0, message)
            }
            if (el.request_due_date === "") {
                message = "Isi kolom Due Date terlebih dahulu (baris "+Number(keys+1)+")"
            }
        })

        return callback(1, message, design, design_item)
    },
}

export default FormValidation
