import React from 'react'
import API from '../../../helpers/API';
import apiconfig from '../../../configs/api.config.json'
import axios from 'axios';
import { Link } from 'react-router-dom'
// import EditAgama from './EditAgama';
import CreateMenu from './CreateMenu';
import { Alert } from 'reactstrap'
import DeleteMenu from './DeleteMenu'
import ViewMenu from './ViewMenu'

class ListMenu extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            showCreateMenu:false,
            menu: [],
            editMenu: false,
            currentMenu: {},
            alertData: {
                status: 0,
                message: ''
            },
            colLength: 0,
            visible: false
        }
        this.closeHandler=this.closeHandler.bind(this)
        this.deleteHandler=this.deleteHandler.bind(this)
        this.deleteModalHandler = this.deleteModalHandler.bind(this)
        this.viewModalHandler = this.viewModalHandler.bind(this)
        this.editModalHandler = this.editModalHandler.bind(this)
        this.modalStatus = this.modalStatus.bind(this)
    }

    async getListMenu(path) {
        let result = await API.getListItem(path);
        this.setState({
            menu: result
        })
    }

    incColLength = () => {
        let num = this.state.colLength
        this.setState({
            colLength: num + 1
        })
    }

    getColLength = () => {
        let token = localStorage.getItem(apiconfig.LS.TOKEN);
        let option = {
            url: apiconfig.BASE_URL + apiconfig.ENDPOINTS.COL_LENGTH + '/m_menu',
            method: 'get',
            headers: {
                "authorization": token,
                "Content-Type": "application/json"
            }
        }
        axios(option)
        .then((response) => {
            if (response.data.code === 200) {
                this.setState({
                    colLength: response.data.message
                })
            } else {
                alert(response.data.message)
            }
        })
    }

    componentDidMount() {
        this.getListMenu(apiconfig.ENDPOINTS.M_MENU);
        this.getColLength()
    }

    deleteModalHandler(menuid) {
        let tmp = {}
        this.state.menu.map((ele) => {
            if (menuid === ele._id) {
                tmp = ele
            }
        })
        this.setState({
            currentMenu : tmp,
            deleteMenu : true
        })
    }

    viewModalHandler(menuid) {
        let tmp = {}
        this.state.menu.map((ele) => {
            if (menuid == ele._id) {
                tmp = ele
            }
        })
        this.setState({
            currentMenu : tmp,
            viewMenu : true
        })
    }

    editModalHandler(menuid) {
        let tmp = {}
        this.state.menu.map((ele) => {
            if (menuid == ele._id) {
                tmp = ({
                    _id: ele._id, 
                    code_edit: ele.code, 
                    name_edit: ele.name,
                    controller_edit: ele.controller,
                    parent_id_edit: ele.parent_id
                })
            }
        })
        this.setState({
            currentMenu : tmp,
            editMenu : true
        })
    }

    closeModalHandler = () => {
        this.getListMenu(apiconfig.ENDPOINTS.M_MENU);
        this.setState({
            viewMenu : false,
            editMenu : false,
            deleteMenu : false    
        })
    }

    showHandler = () => {
        this.setState({showCreateMenu:true})
    }

    closeHandler(){
        this.getListMenu(apiconfig.ENDPOINTS.M_MENU);
        this.setState({showCreateMenu:false})
    }

    deleteHandler(param){
        let token = localStorage.getItem(apiconfig.LS.TOKEN)
        let option = {
            url: apiconfig.BASE_URL+apiconfig.ENDPOINTS.AGAMA+'/'+param,
            method: "delete",
            headers: {
                "Authorization": token
            }
        }
        axios(option)
        .then((response)=>{
            let currentindex = -1
            this.state.agama.map((ele, idx)=>{
                if(ele._id==param){
                    currentindex=idx
                    this.props.history.goBack()
                }
            })
            let tmp=this.state.agama
            tmp.splice(currentindex,1)
            this.setState({
                agama: tmp
            })
        })
        .catch((error)=>{
            console.log(error)
        })
    }

    setFade = () => {
        alert('masuk')

    }

    modalStatus(status, message) {
        this.setState({
            alertData: {
                status: status,
                message: message,
            },
            visible: true
        })
        setTimeout(() => {
            this.setState({
                visible: false
            })
        }, 5000)
    }

    render(){

    return (
        <div>
        <ul class="breadcrumb">
            <li><a href="/">Home</a> <span class="divider">/</span></li>
            <li><a href="/menu">Master</a> <span class="divider">/</span></li>
            <li class="active">List Menu</li>
        </ul>

        <div className="container">
            <h4>List Menu</h4>

            {
                this.state.alertData.status === 1 ?
                    <Alert isOpen={this.state.visible} className="alert-success">
                        <b>Data Deleted!</b> Data menu with code <b>
                        {this.state.alertData.message}
                        </b> has been deleted!
                        </Alert>
                    : ''
            }
            {
                this.state.alertData.status === 2 ?
                    <Alert isOpen={this.state.visible}>
                        Deleting data <b> {this.state.alertData.message} </b> unsuccessfull
                        </Alert>
                    : ''
            }

                <button type="button" class="btn btn-primary float-right"
                onClick ={this.showHandler}> Add </button>

                <form class="form-inline">
                    <input type="text" className="form-control col-sm-11" placeholder="Buku Code" id="text"/>
                </form>

                <CreateMenu
                show = {this.state.showCreateMenu}
                closeHandler={this.closeHandler}
                colLength = {this.state.colLength}
                incCol = {this.incColLength} />
                <ViewMenu
                view = {this.state.viewMenu}
                closeModalHandler = {this.closeModalHandler} 
                menu = {this.state.currentMenu}/>
                <DeleteMenu
                delete = {this.state.deleteMenu}
                menu = {this.state.currentMenu}
                closeModalHandler = {this.closeModalHandler}
                modalStatus = {this.modalStatus}/>
                {/* <EditAgama
                edit = {this.state.editAgama}
                closeModalHandler = {this.closeModalHandler}
                agama = {this.state.currentAgama}
                modalStatus = {this.modalStatus}
                loadAgama = {this.getListMenu}/> */}
            </div>

                <div className="container" style={{paddingTop: '40px'}}>
                <table class="table table-hover">
                    <thead>
                    <tr>
                        <th>No</th>
                        <th>Code</th>
                        <th>Menu Name</th>
                        <th>Created Date</th>
                        <th>Create By</th>  
                        <th>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                       {
                          this.state.menu.map((ele,x)=>
                                <tr>
                                  <td>{x+1}</td>
                                  <td>{ele.code}</td>
                                  <td>{ele.name}</td>
                                  <td>{ele.created_date}</td>
                                  <td>{ele.created_by}</td>
                                  <td>
                                  <Link to='#'>
                       <span onClick = {() => {this.viewModalHandler(ele._id)}} className="fa fa-search" style={{fontSize : '18px', paddingRight : '30px'}} />    

                      <span onClick = {() => {this.editModalHandler(ele._id)}} class="fa fa-edit" style={{fontSize : '18px', paddingRight : '30px'}} />            

                         <span onClick = {() => {this.deleteModalHandler(ele._id)}} class="fa fa-trash" style={{fontSize : '18px'}} />

                      </Link>
                    </td>
                                </tr>



                            )
                       }
                    </tbody>
                </table>
                </div>
                </div>
        )
    }
}

export default ListMenu
