import React from 'react'
import { Modal, ModalBody, ModalFooter, ModalHeader, Button } from 'reactstrap'
import axios from 'axios'
import apiconfig from '../../../configs/api.config.json'

class EditMenu extends React.Component {
    constructor(props) {
        super(props)
        let userdata =JSON.parse(localStorage.getItem(apiconfig.LS.USERDATA))     
        this.state = {
            formdata: {
                code_edit: '', 
                name_edit: '',
                controller_edit: '',
                parent_id_edit: '',
                updated_by: userdata.username
            }
        }
    }

    changeHandler = (e) => {
        let tmp = this.state.formdata
        tmp[e.target.name] = e.target.value
        this.setState({
            formdata:tmp
        })
    }

    componentWillReceiveProps(data) {
        this.setState({
            formdata:data.menu,
        })
        console.log(this.state.formdata)
    }

    submitHandler = () => {
        let token = localStorage.getItem(apiconfig.LS.TOKEN)
        let option = {
            url: apiconfig.BASE_URL+apiconfig.ENDPOINTS.AGAMA,
            method: "put",
            headers: {
                "authorization": token,
                "Content-Type" : "application/json"
            },
            data: this.state.formdata
        }
        axios(option)
        .then((response) => {
            if(response.data.code === 200) {
                this.props.closeModalHandler()
                alert('Success')
                this.props.history.push('/')
            } else {
                alert(response.data.message)
            }
        })
        .catch((error) => {
            console.log(error);            
        })
    }

    render(){
        return(
            <Modal isOpen={this.props.edit} className={this.props.className}>
                <ModalHeader className="bg-primary"> Edit Menu - {this.state.formdata.name_edit} ({this.state.formdata.code_edit}) </ModalHeader>
                <ModalBody>
                <table>
                    <tr>
                        <td>Kode Menu</td>
                        <td style={{padding: '0 30px'}}>:</td>
                        <td>
                            <div class ="input-group input-group-sm">
                                <input type="text" class="form-control" placeholder="Kode menu"
                                name="code_edit" value={this.state.formdata.code_edit} onChange={this.changeHandler} required />
                            </div>
                        </td>
                    </tr>
                    <tr>
                        <td>Deskripsi</td>
                        <td style={{padding: '0 30px'}}>:</td>
                        <td>
                            <div class ="input-group mb-3 input-group-sm">
                                <input type="text" class="form-control" placeholder="Deskripsi" 
                                name="name_edit" value={this.state.formdata.name_edit} onChange={this.changeHandler} required/>
                            </div>
                        </td>
                    </tr>
                </table>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick ={this.submitHandler}>Save</Button>
                    <Button color="warning" onClick={this.props.closeModalHandler}>Cancel</Button>
                </ModalFooter>
            </Modal>
        )
    }
}
export default EditMenu
