import React from 'react'
import { Modal, ModalBody, ModalFooter, ModalHeader, Button } from 'reactstrap'
import axios from 'axios'
import apiconfig from '../../../configs/api.config.json'

class EditAgama extends React.Component {
    constructor (props) {
        super(props)
        // let userdata=""
        // if(localStorage.getItem(apiconfig.LS.TOKEN)!= null){
         let   userdata =JSON.parse(localStorage.getItem(apiconfig.LS.USERDATA))
       // }        
        this.state = {
            formdata: {
                kode_agama_edit: '', 
                deskripsi_edit: '',
                updated_by :userdata.username
            }
        }
        this.submitHandler = this.submitHandler.bind(this)
        this.changeHandler = this.changeHandler.bind(this)
    }

    changeHandler(e) {
        let tmp = this.state.formdata
        tmp[e.target.name] = e.target.value
        this.setState({
            formdata:tmp
        })
    }

    componentWillReceiveProps(data) {
        this.setState({
            formdata:data.agama,
        })

        console.log(data.agama)
    }

    submitHandler() {
        let token = localStorage.getItem(apiconfig.LS.TOKEN)
        let option = {
            url: apiconfig.BASE_URL+apiconfig.ENDPOINTS.AGAMA,
            method: "put",
            headers: {
                "authorization": token,
                "Content-Type" : "application/json"
            },
            data: this.state.formdata
        }
        axios(option)
        .then((response) => {
            if(response.data.code === 200) {
                this.props.closeModalHandler()
                alert('Success')
                this.props.history.push('/')
            } else {
                alert(response.data.message)
            }
        })
        .catch((error) => {
            console.log(error);            
        })
    }

    render(){
        return(
            <Modal isOpen={this.props.edit} className={this.props.className}>
                <ModalHeader> Edit Agama</ModalHeader>
                <ModalBody>
                <table>
                    <tr>
                        <td style={{width: '40%'}}>Kode Agama</td>
                        <td style={{width: '10%'}}>:</td>
                        <td style={{width: '50%'}}>
                            <div class ="input-group mb-3 input-group-sm">
                                <input type="text" class="form-control" placeholder="Kode agama"
                                name="kode_agama_edit" value={this.state.formdata.kode_agama_edit} onChange={this.changeHandler} required />
                            </div>
                        </td>
                    </tr>
                    <tr>
                        <td>Deskripsi</td>
                        <td>:</td>
                        <td>
                            <div class ="input-group mb-3 input-group-sm">
                                <input type="text" class="form-control" placeholder="Deskripsi" 
                                name="deskripsi_edit" value={this.state.formdata.deskripsi_edit} onChange={this.changeHandler} required/>
                            </div>
                        </td>
                    </tr>
                </table>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick ={this.submitHandler}>Save</Button>
                    <Button color="warning" onClick={this.props.closeModalHandler}>Cancel</Button>
                </ModalFooter>
            </Modal>
        )
    }
}
export default EditAgama
