import React from 'react'
import API from '../../../helpers/API';
import apiconfig from '../../../configs/api.config.json'
import axios from 'axios';
import { Link } from 'react-router-dom'
// import EditAgama from './EditAgama';
// import CreateAgama from './CreateAgama';
import { Alert } from 'reactstrap'
// import DeleteAgama from './DeleteAgama'
// import ViewAgama from './ViewMenu'

class ListAgama extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            showCreateAgama:false,
            menu: [],
            editAgama: false,
            currentAgama: {},
            alertData: {
                status: 0,
                message: ''
            }
        }
        this.showHandler=this.showHandler.bind(this)
        this.closeModalHandler = this.closeModalHandler.bind(this)
        this.closeHandler=this.closeHandler.bind(this)
        this.deleteHandler=this.deleteHandler.bind(this)
        this.deleteModalHandler = this.deleteModalHandler.bind(this)
        this.viewModalHandler = this.viewModalHandler.bind(this)
        this.editModalHandler = this.editModalHandler.bind(this)
        this.modalStatus = this.modalStatus.bind(this)
    }

    async getListMenu(path) {
        let result = await API.getListItem(path);
        this.setState({
            menu: result
        })
    }

    componentDidMount() {
        this.getListMenu(apiconfig.ENDPOINTS.MENU);
    }

    deleteModalHandler(agamaid) {
        let tmp = {}
        this.state.agama.map((ele) => {
            if (agamaid === ele._id) {
                tmp = ele
            }
        })
        this.setState({
            currentAgama : tmp,
            deleteAgama : true
        })
    }

    viewModalHandler(agamaid) {
        let tmp = {}
        this.state.agama.map((ele) => {
            if (agamaid == ele._id) {
                tmp = ele
            }
        })
        this.setState({
            currentAgama : tmp,
            viewAgama : true
        })
    }

    editModalHandler(agamaid) {
        let tmp = {}
        this.state.agama.map((ele) => {
            if (agamaid == ele._id) {
                tmp = ({
                    _id: ele._id, 
                    kode_agama_edit: ele.kode_agama, 
                    deskripsi_edit: ele.deskripsi
                })
            }
        })
        this.setState({
            currentAgama : tmp,
            editAgama : true
        })
    }

    closeModalHandler() {
        this.setState({
            viewAgama : false,
            editAgama : false,
            deleteAgama : false    
        })
    }

    showHandler(){
        this.setState({showCreateAgama:true})
    }

    closeHandler(){
        this.setState({showCreateAgama:false})
    }

    deleteHandler(param){
        let token = localStorage.getItem(apiconfig.LS.TOKEN)
        let option = {
            url: apiconfig.BASE_URL+apiconfig.ENDPOINTS.AGAMA+'/'+param,
            method: "delete",
            headers: {
                "Authorization": token
            }
        }
        axios(option)
        .then((response)=>{
            let currentindex = -1
            this.state.agama.map((ele, idx)=>{
                if(ele._id==param){
                    currentindex=idx
                    this.props.history.goBack()
                }
            })
            let tmp=this.state.agama
            tmp.splice(currentindex,1)
            this.setState({
                agama: tmp
            })
        })
        .catch((error)=>{
            console.log(error)
        })
    }
    modalStatus(status, message) {
        this.setState({
            alertData : {
                status : status,
                message : message
            }   
        })
    }

    render(){

    return (
        <div>
        <ul class="breadcrumb">
            <li><a href="/">Home</a> <span class="divider">/</span></li>
            <li><a href="/menu">Master</a> <span class="divider">/</span></li>
            <li class="active">List Agama</li>
        </ul>

        <div class="container">
            <h4>List Agama</h4>

             {
                (this.state.alertData.status == 1) ? <Alert color ="success"> {this.state.alertData.message} </Alert>:''
            }
            {
                (this.state.alertData.status == 2) ? <Alert color ="danger"> {this.state.alertData.message} </Alert>: ''
            }


                <button type="button" class="btn btn-primary float-right"
                onClick ={this.showHandler}> Add </button>
                {/* <CreateAgama
                show = {this.state.showCreateAgama}
                closeHandler={this.closeHandler} />
               <ViewAgama
                view = {this.state.viewAgama}
                closeModalHandler = {this.closeModalHandler} 
                agama = {this.state.currentAgama}
                />
                <DeleteAgama
                delete = {this.state.deleteAgama}
                agama = {this.state.currentAgama}
                closeModalHandler = {this.closeModalHandler}
                modalStatus = {this.modalStatus}
                />
                <EditAgama
                edit = {this.state.editAgama}
                closeModalHandler = {this.closeModalHandler}
                agama = {this.state.currentAgama}
                modalStatus = {this.modalStatus}
                loadAgama = {this.getListAgama}
                /> */}
                <br/> <br/>
                <form class="form-inline">
                <div class ="container">
                <input type="text" placeholder="Buku Code" class="col-sm-3 padding=5px" id="text"/>
                <input type="text" placeholder="Buku Name" class="col-sm-3 padding=5px" id="text"/>
                <input type="date" placeholder="Created date" class="col-sm-3 padding=5px datepicker" id="date"/>
                <input type="text" placeholder="Created By" class="col-sm-2 padding=5px" id="text"/>

                <button type="button" class="btn btn-warning float-right " 
                onClick ={this.searchHandler}> Search </button>
                </div>
                </form>
                <table class="table table-hover">
                    <thead>
                    <tr>
                        <th>No</th>
                        <th>Kode Agama</th>
                        <th>Deskripsi</th>  
                        <th>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                       {

                           //this.state.buku.map((ele,x)=>
                            //let tmp=1

                            //let number=this.state.company.length
                            //console.log(number)
                            // // tmp+=1
                            //   while(tmp<=number)
                            //   {
                            //       tmp+1
                            //   }
                                // return <SingleSouvRow 
                                // no = {x+1}
                                // code={ele.code}
                                // name={ele.name}
                                // _id={ele._id}
                                // created_date={ele.created_date}
                                // created_by={ele.created_by}
                                // viewModalHandler={this.viewModalHandler}
                                // editModalHandler={this.editModalHandler}
                                // deleteModalHandler={this.deleteModalHandler}
                                // /> 

                            // }//tmp+=1
                            this.state.agama.map((ele,x)=>
                                <tr>
                                  <td>{x+1}</td>
                                  <td>{ele.kode_agama}</td>
                                  <td>{ele.deskripsi}</td>
                                  <td>
                                  <Link to='#'>
                       <span onClick = {() => {this.viewModalHandler(ele._id)}} className="fa fa-search" style={{fontSize : '18px', paddingRight : '30px'}} />    

                      <span onClick = {() => {this.editModalHandler(ele._id)}} class="fa fa-edit" style={{fontSize : '18px', paddingRight : '30px'}} />            

                         <span onClick = {() => {this.deleteModalHandler(ele._id)}} class="fa fa-trash" style={{fontSize : '18px'}} />

                      </Link>
                    </td>
                                </tr>



                            )
                       }
                    </tbody>
                </table>
                </div>
                </div>
        )
    }
}

export default ListAgama
